new_hash = {}
j_sort = []
j.each do |w|
  j_sort.push(w.length)
end
counter = j_sort.sort.max + 1
counter.times do |i|
    if j_sort.count(i) != 0
      if j_sort.count(i) == 1
        puts "Il y a #{j_sort.count(i)} handle qui fait #{i} caractères de long !"
      else
        puts "Il y a #{j_sort.count(i)} handle(s) qui font #{i} caractères de long !"
      end
      new_hash.store(i,j_sort.count(i))
    end
  end

